import { Component } from '@angular/core';
import { NgxQRCodeModule } from 'ngx-qrcode2';

@Component({
  selector: 'app-upi',
  templateUrl: './upi.component.html',
  styleUrls: ['./upi.component.css'],
  imports: [
    NgxQRCodeModule,
  ]
})
export class UPIComponent {

  paymentQRCode: string = 'https://buy.stripe.com/test_5kA8y6dvP9zL5heaEE'; // Replace with your QR code data
  paymentSuccess: boolean = false;
  paymentFailure: boolean = false;

  initiatePayment() {
    // Simulated payment success/failure after scanning the QR code
    setTimeout(() => {
      const paymentSuccess = Math.random() < 0.8; // Simulating 80% success rate
      this.handlePaymentResult(paymentSuccess);
    }, 5000); // Simulating a delay - replace with actual success/failure handling logic
  }

  handlePaymentResult(success: boolean) {
    if (success) {
      this.paymentSuccess = true;
      this.paymentFailure = false;
    } else {
      this.paymentSuccess = false;
      this.paymentFailure = true;
    }
  }
}
